import numpy as npdef integrate(N, a, b):    def f(x):        '''type function'''        return x**2    value=0    value2=0    for n in range(1, N+1):        value += f(a+((n-(1/2))*((b-a)/N)))    value2 = ((b-a)/N)*value    return value2def main():        N = 100    a = 0    b = 1    print("............")    print("Here is the answer")    print(integrate(N, a, b))    main()